public class PKL_ZadanieKalkulator : ZadanieKalkulatorBase {
	public PKL_ZadanieKalkulator(Zadanie zadanie)
		: base(zadanie) {
	}


	public Zadanie Zadanie {
		get {
			return zadanie;
		}
	}


	public DefZadania ZadaniePowiazane () {
		if(Zadanie.Features.GetBool("Generuj zdarzenie"))
		{

		using (ITransaction IT=Zadanie.Session.Logout(true))
                     {

		Zadanie.Features["Bufor inicjowania opisu"]=Zadanie.Uwagi.ToString()  ;


	                     IT.Commit();

		 }


		return Zadanie.Definicja.Powiazane ;
		}
		return Zadanie.CzyZamykane ? Zadanie.Definicja.Powiazane : null;
	}


/*
	public void StanZadania_Changed(StanZadania Poprzedni) {
	}
*/

